import React, { useState, useEffect } from "react";
import { Canvas } from "./components/Canvas";
import { DoneCommands } from "./components/DoneCommands";
import { actionAddCommandLine } from "./store/actions/lines";
import { useSelector, useDispatch } from "react-redux";
import { actionChangeCanvasHeight, actionChangeCanvasWidth } from "../store/actions/canvas"

export const Inputes = () => {
  const dispatch = useDispatch();
  const addCommandLine = line => dispatch(actionAddCommandLine(line));
  const changeCanvasHeigth = ( height ) => dispatch(actionChangeCanvasHeight( height ));
  const changeCanvasWidth = ( width ) => dispatch(actionChangeCanvasHeight( width ));
  const [valueTextArea, setValueTextArea] = useState("");

  const onChangeTextArea = str => {
    if (str[str.length - 1] === "\n") {
      addCommandLine(str);
      console.log(str);
      str = "";
    }
    setValueTextArea(str);
  };
  return (
    <div className="inputes">
      <h1>Draw app</h1>
      <p>Create your picture</p>
      <p>Enter size of canvas, x</p>
      <input
        type="text"
        placeholder="Width"
        onChange={e => setWidthCanvas(e.target.value)}
      ></input>
      <input
        type="text"
        placeholder="Height"
        onChange={e => setHeightCanvas(e.target.value)}
      ></input>
      <textarea
        type="text"
        placeholder="Enter commands"
        value={valueTextArea}
        onChange={e => onChangeTextArea(e.target.value)}
      ></textarea>
      <DoneCommands />
    </div>
  );
};
