import React, { useState, useEffect } from "react";
import { Lines } from "./components/Lines";
import { Canvas } from "./components/Canvas";
import { Rectangles } from "./components/Rectangles";
import { useSelector, useDispatch } from "react-redux";
import { actionAddLine } from "./store/actions/lines";
import { actionChangeCanvas } from "./store/actions/canvas";
import { actionAddRectangle } from "./store/actions/rectangles";

import "./App.css";

export const App = () => {
  const dispatch = useDispatch();
  const addLine = () => dispatch(actionAddLine(null));
  const addRectangle = () => dispatch(actionAddRectangle(null));
  const changeCanvas = ({ width, height }) =>
    dispatch(actionChangeCanvas({ width, height }));
  const [widthCanvas, setWidthCanvas] = useState(undefined);
  const [heightCanvas, setHeightCanvas] = useState(undefined);

  useEffect(() => {
    changeCanvas({ width: widthCanvas, height: heightCanvas });
  }, [widthCanvas, heightCanvas]);

  return (
    <div className="main-body">
      <div className="inputes">
        <h1>Draw app</h1>
        <p>Create your picture</p>
        <p>Enter size of canvas</p>
        <input
          type="text"
          placeholder="Width"
          onChange={e => setWidthCanvas(e.target.value)}
        ></input>
        <input
          type="text"
          placeholder="Height"
          onChange={e => setHeightCanvas(e.target.value)}
        ></input>
        <Lines />
        <button className="add-figure" onClick={addLine}>
          <i className="fa fa-plus" aria-hidden="true"></i> Add new line
        </button>
        <Rectangles />
        <button className="add-figure" onClick={addRectangle}>
          <i className="fa fa-plus" aria-hidden="true"></i> Add new rectangle
        </button>
      </div>
      {widthCanvas !== undefined && heightCanvas !== undefined ? (
        <Canvas />
      ) : null}
    </div>
  );
};
