import React from "react";
import { useSelector } from "react-redux";
import "../App.css";

export const Canvas = () => {
    const { canvas } = useSelector(state => state);
    // document.body.clientWidth-250-25
    return (
        <div className="canvas" style={{height:`${canvas.height}ex`, width: `${canvas.width*9/10}em`}}>
            <span className="try" style={{top:"calc(10ex - 1em)"}}>xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx</span>
            
        </div>
    );
}
