import { ACTION_CHANGE_CANVAS_HEIGHT, ACTION_CHANGE_CANVAS_WIDTH, ACTION_CREATE_CANVAS } from "./actionTypes";
import { actionSetCanvasValue, actionSetRequestError } from "./commands"

export const actionChangeCanvasHeight = (height) => {
    return {
        type: ACTION_CHANGE_CANVAS_HEIGHT,
        payload: height,
    }
}

export const actionChangeCanvasWidth = (width) => {
    return {
        type: ACTION_CHANGE_CANVAS_WIDTH,
        payload: width,
    }
}

export const actionCreateCanvas = ({widthCanvas, heightCanvas}) => dispatch => {  
    fetch("http://localhost:3001/createCanvas", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8"
      },
      body: JSON.stringify({widthCanvas, heightCanvas}),
      // mode: "no-cors"
    })
      .then(result =>
        result.json().then(data => {
          dispatch({
            type: ACTION_CREATE_CANVAS,
            payload: {widthCanvas, heightCanvas}
          });
          dispatch(actionSetCanvasValue(data));
          dispatch(actionSetRequestError(""));
        }),
      )
      .catch(err => dispatch(actionSetRequestError("Wrong command")));
        
  };
