import React, { useState, useEffect } from "react";
import { LineInputes } from "./components/LineInputes";
import { Canvas } from "./components/Canvas";
import { RectangleInputes } from "./components/RectangleInputes";
import { useSelector, useDispatch } from "react-redux";
import { actionAddInputLine } from "./store/actions/lines";
import { actionChangeCanvas } from "./store/actions/canvas";
import { actionAddInputRectangle } from "./store/actions/rectangles";

import "./App.css";

export const App = () => {
  const dispatch = useDispatch();
  const addInputLine = () => dispatch(actionAddInputLine(null));
  const addInputRectangle = () => dispatch(actionAddInputRectangle(null));
  const changeCanvas = ({ width, height }) =>
    dispatch(actionChangeCanvas({ width, height }));
  const addLine= () => dispatch(actionAddLine(null));

  const [widthCanvas, setWidthCanvas] = useState("");
  const [heightCanvas, setHeightCanvas] = useState("");

  useEffect(() => {
    changeCanvas({ width: widthCanvas, height: heightCanvas });
  }, [widthCanvas, heightCanvas]);

  // alert(document.body.clientWidth-250-25)

  return (
    <div className="main-body">
      <div className="inputes">
        <h1>Draw app</h1>
        <p>Create your picture</p>
        <p>Enter size of canvas</p>
        <input
          type="text"
          placeholder="Width"
          onChange={e => setWidthCanvas(e.target.value)}
        ></input>
        <input
          type="text"
          placeholder="Height"
          onChange={e => setHeightCanvas(e.target.value)}
        ></input>
        <LineInputes />
        <button className="add-figure" onClick={addInputLine}>
          <i className="fa fa-plus" aria-hidden="true"></i> Add new line
        </button>
        <RectangleInputes />
        <button className="add-figure" onClick={addInputRectangle}>
          <i className="fa fa-plus" aria-hidden="true"></i> Add new rectangle
        </button>
      </div>
      {widthCanvas !== "" && heightCanvas !== "" ? (
        <Canvas />
      ) : null}
    </div>
  );
};
