import {
  ACTION_ADD_COMMAND_LINE,
  ACTION_CHANGE_CANVAS_HEIGHT,
  ACTION_CHANGE_CANVAS_WIDTH,
  ACTION_SET_CANVAS_VALUE,
} from "../actions/actionTypes";

const initialState = {
  commands: [],
  lines: [],
  canvas: { width: 0, height: 0, value: [] },
};

export const figureReduser = (state = initialState, action) => {
  switch (action.type) {
    case ACTION_ADD_COMMAND_LINE:
      return { ...state, commands: [action.payload, ...state.commands] };
    case ACTION_CHANGE_CANVAS_WIDTH:
      return {
        ...state,
        canvas: { ...state.canvas, width: action.payload }
      };
    case ACTION_CHANGE_CANVAS_HEIGHT:
      return {
        ...state,
        canvas: { ...state.canvas, height: action.payload }
      };
      case ACTION_SET_CANVAS_VALUE:
      return {
        ...state,
        canvas: { ...state.canvas, value: action.payload }
      };
    default:
      return {
        ...state
      };
  }
};
