import React from "react";
import { useSelector } from "react-redux";
import "../App.css";

export const Lines = () => {
    const createLine = (x1,y1,x2,y2) => {
        const num = y1===y2?Math.abs(x2-x1):Math.abs(y2-y1);
        num+=2;
        const degree = y1===y2?90:0;
        let line = {
            str: "x".repeat(num),
            deg: degree
        }

    }
    // document.body.clientWidth-250-25
    return;
}
