import React, { useState } from "react";
import { useSelector } from "react-redux";
import "../App.css";

export const LineInputes = () => {
  const { linesInput } = useSelector(state => state);
  const [values, setValues] = useState([]);
  const allValues = [];
  let obj ={};

  const setValueFrom = (str, index) => {
    console.log(allValues[index]);
    

    if (allValues[index] === undefined) {
      allValues.push(obj)
      console.log(allValues);
    }
    // allValues.push({ [index]: { [str.placeholder]: str.value } });

    allValues[index][str.placeholder] = str.value;
     obj= allValues[index][str.placeholder];
    console.log(allValues);

    // setValues([values, (values[index].from ={ [str.placeholder]: str.value } })] );
  };

  const setValueTo = (str, index) => {
    setValues((values[index] = { to: { [str.placeholder]: str.value } }));
    console.log(values);
  };
  return (
    <div>
      {linesInput.map((line, index) => (
        <div className="line-inputes" key={index}>
          <h3>Line {index}: </h3>
          <p>First point</p>
          <input
            type="text"
            placeholder="X"
            onChange={e => setValueFrom(e.target, index)}
          ></input>
          <input
            type="text"
            placeholder="Y"
            onChange={e => setValueFrom(e.target, index)}
          ></input>
          <p>Second point</p>
          <input
            type="text"
            placeholder="X"
            onChange={e => setValueTo(e.target, index)}
          ></input>
          <input
            type="text"
            placeholder="Y"
            onChange={e => setValueTo(e.target, index)}
          ></input>
        </div>
      ))}
    </div>
  );
};
