import React from "react";
import { Lines } from "./components/Lines";
import { Canvas } from "./components/Canvas";
import { Rectangles } from "./components/Rectangles";
import { useSelector, useDispatch } from "react-redux";
import { actionAddLine } from "./store/actions/lines"
import { actionAddRectangle } from "./store/actions/rectangles"

import "./App.css";

export const App = () => {
  const dispatch = useDispatch();
  const addLine = () => dispatch(actionAddLine(null));
  const addRectangle = () => dispatch(actionAddRectangle(null));


  return (
    <div className="main-body">
      <div className="inputes">
      <h1>Draw app</h1>
      <p>Create your picture</p>
      <p>Enter size of canvas</p>
      <input type="text" placeholder="Width"></input>
      <input type="text" placeholder="Height"></input>
      <button className="add-figure" onClick={addLine}>
        <i className="fa fa-plus" aria-hidden="true"></i> Add new line
      </button>
      <Lines />
      <button className="add-figure" onClick={addRectangle}>
        <i className="fa fa-plus" aria-hidden="true"></i> Add new rectangle
      </button>
      <Rectangles />
      </div>
      <Canvas />
    </div>
  );
};
