import { ACTION_ADD_LINE, ACTION_ADD_RECTANGLE } from "../actions/actionTypes";

const initialState = {
  lines: [],
  rectangle: []
};

export const figureReduser = (state = initialState, action) => {
  switch (action.type) {
    case ACTION_ADD_LINE:
      return { ...state, lines: [...state.lines, action.payload] };
    case ACTION_ADD_RECTANGLE:
      return { ...state, rectangle: [...state.rectangle, action.payload] };
    default: {
      return {
        ...state
      };
    }
  }
};
