import React, { useState } from "react";
import { useSelector } from "react-redux";
import "../App.css";

export const LineInputes = () => {
  const { linesInput } = useSelector(state => state);
  const [values, setValues] = useState([]);

  const setValueFrom = (str, index) =>{
    setValues([[index]={to:str}]);
    console.log(values);
  }

  const setValueTo = (str, index) =>{
    setValues([[index]={to:str}]);
    console.log(values);
  }
  return (
    <div>
      {linesInput.map((line, index) => (
        <div className="line-inputes" key={index}>
          <h3>Line {index}: </h3>
          <p>First point</p>
          <input
            type="text"
            placeholder="X"
            onChange={e =>
              setValueFrom(e.target.value, index)
            }
          ></input>
          <input type="text" placeholder="Y"></input>
          <p>Second point</p>
          <input type="text" placeholder="X"></input>
          <input type="text" placeholder="Y"></input>
        </div>
      ))}
    </div>
  );
};
