import React, { useState, useEffect } from "react";
import { Canvas } from "./components/Canvas";
import { useSelector, useDispatch } from "react-redux";
import { actionAddCommandLine } from "./store/actions/lines";
import { actionChangeCanvas } from "./store/actions/canvas";
// import { actionAddInputRectangle } from "./store/actions/rectangles";

import "./App.css";

export const App = () => {
  const dispatch = useDispatch();
  const addCommandLine = line => dispatch(actionAddCommandLine(line));
  const changeCanvas = ({ width, height }) =>
    dispatch(actionChangeCanvas({ width, height }));
  const [widthCanvas, setWidthCanvas] = useState("");
  const [heightCanvas, setHeightCanvas] = useState("");
  const [valueTextArea, setValueTextArea] = useState("");

  useEffect(() => {
    changeCanvas({ width: widthCanvas, height: heightCanvas });
  }, [widthCanvas, heightCanvas]);

  const onChangeTextArea = str => {
    if (str[str.length - 1] === "\n") {
      addCommandLine(str);
      console.log(str);
      str = "";
    }
    setValueTextArea(str);
  };

  return (
    <div className="main-body">
      <div className="inputes">
        <h1>Draw app</h1>
        <p>Create your picture</p>
        <p>Enter size of canvas, x</p>
        <input
          type="text"
          placeholder="Width"
          onChange={e => setWidthCanvas(e.target.value)}
        ></input>
        <input
          type="text"
          placeholder="Height"
          onChange={e => setHeightCanvas(e.target.value)}
        ></input>
        <textarea
          type="text"
          placeholder="Enter commands"
          value={valueTextArea}
          onChange={e => onChangeTextArea(e.target.value)}
        ></textarea>
      </div>
      {widthCanvas !== "" && heightCanvas !== "" ? <Canvas /> : null}
    </div>
  );
};
