import {
  ACTION_ADD_COMMAND_LINE,
  ACTION_CHANGE_CANVAS
} from "../actions/actionTypes";

const initialState = {
  linesInput: [],
  lines: [],
  rectanglesInput: [],
  canvas: { width: 0, height: 0 }
};

export const figureReduser = (state = initialState, action) => {
  switch (action.type) {
    case ACTION_ADD_COMMAND_LINE:
      return { ...state, linesInput: [...state.linesInput, action.payload] };
    case ACTION_CHANGE_CANVAS:
      return {
        ...state,
        canvas: { width: action.payload.width, height: action.payload.height }
      };
    default: {
      return {
        ...state
      };
    }
  }
};
