import React, { useState, useEffect } from "react";
import { Inputes } from "./components/Inputes";
import { Canvas } from "./components/Canvas";
// import { actionAddInputRectangle } from "./store/actions/rectangles";

import "./App.css";

export const App = () => {
  const { width, height } = useSelector(state => state.canvas);

  useEffect(() => {
    changeCanvas({ width, height });
  }, [width, height]);

  return (
    <div className="main-body">
      <Inputes />
      <Canvas />
    </div>
  );
};
