import React, { useState, useEffect } from "react";
import { Canvas } from "./components/Canvas";
import { DoneCommands } from "./components/DoneCommands";
import { useSelector, useDispatch } from "react-redux";
import { actionChangeCanvas } from "./store/actions/canvas";

export const Inputes = () => {
  const dispatch = useDispatch();
  const changeCanvas = ({ width, height }) =>
    dispatch(actionChangeCanvas({ width, height }));
  return (
    <div className="inputes">
      <h1>Draw app</h1>
      <p>Create your picture</p>
      <p>Enter size of canvas, x</p>
      <input
        type="text"
        placeholder="Width"
        onChange={e => setWidthCanvas(e.target.value)}
      ></input>
      <input
        type="text"
        placeholder="Height"
        onChange={e => setHeightCanvas(e.target.value)}
      ></input>
      <textarea
        type="text"
        placeholder="Enter commands"
        value={valueTextArea}
        onChange={e => onChangeTextArea(e.target.value)}
      ></textarea>
      <DoneCommands />
    </div>
  );
};
